export function addTask(data) {
  return {
    type: '@user/UPDATE_TASK_REQUEST',
    payload: { data },
  };
}

export function addTaskSuccess(profile) {
  return {
    type: '@user/UPDATE_TASK_SUCCESS',
    payload: { profile },
  };
}

export function addTaskFailure() {
  return {
    type: '@user/UPDATE_TASK_REQUEST',
  };
}
