import React, { useState, useEffect } from 'react';
import Moment from 'react-moment';
import { FaEdit, FaTrash } from "react-icons/fa";

import api from '~/services/api';

import { Container, Task } from './styles';

export default function Dashboard() {

  const [schedule, setSchedule] = useState([]);
  const [date] = useState(new Date());

  useEffect(() => {
    async function loadSchedule() {
      const _dataHeaders = JSON.parse(localStorage.getItem('headers'));
      const response = await api.get('/tasks', {headers: _dataHeaders});
      localStorage.setItem('headers', JSON.stringify(response.headers));
      setSchedule(response.data);
    }
    loadSchedule();
  }, [date]);

  return (
    <Container>
      <button type="Button" onClick={() => this.setState({ show: true })}>ADICIONAR TAREFA</button>

      <ul>
        {schedule.map(task => (
          <Task key={task.id}>
            <header>
              <title>{task.name}</title>
              <actions>
                <FaEdit className="edit" />
                <FaTrash className="delete" />
              </actions>
            </header>

            <span>{task.description}</span>
            <small>
              <Moment format="DD/MM/YYYY">{task.end_time}</Moment>
            </small>
          </Task>
        ))}
      </ul>
    </Container>
  );
}
