import React, { useState, button, useEffect } from 'react';
import Moment from 'react-moment';

import api from '~/services/api';

import { Container, Time } from './styles';

export default function Dashboard() {
  const [schedule, setSchedule] = useState([]);
  const [date] = useState(new Date());

  useEffect(() => {
    async function loadSchedule() {
      const _dataHeaders = JSON.parse(localStorage.getItem('headers'));
      const response = await api.get('/tasks', {headers: _dataHeaders});
      localStorage.setItem('headers', JSON.stringify(response.headers));
      setSchedule(response.data);
    }
    loadSchedule();
  }, [date]);

  function AddTask() {}

  return (
    <Container>
      <button type="Button" onClick={AddTask}>ADICIONAR TAREFA</button>

      <ul>
        {schedule.map(task => (
          <Task key={task.id}>
            <strong>{task.name}</strong>
            <span>{task.description}</span>
            <small>
              <Moment format="DD/MM/YYYY">{task.end_time}</Moment>
            </small>
          </Task>
          // <Time key={time.time} past={time.past} available={!time.appointment}>
          //   <strong>{time.time}</strong>
          //   <span>
          //     {time.appointment ? time.appointment.user.name : 'Em aberto'}
          //   </span>
          // </Time>
        ))}
      </ul>
    </Container>
  );
}
