import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

import { Form, Input } from '@rocketseat/unform';
import { addTaskRequest } from '~/store/modules/task/actions';

import { Container } from './styles';
import history from '~/services/history';
import session from '~/helpers/session';
import api from '~/services/api';

export default function Task() {
  const queryString = window.location.search.slice(1).split('?');
  const task = {};
  if (queryString) {
    concat(task, loadSchedule());
  }
  const dispatch = useDispatch();

  const handleSubmitTask = data => {
    dispatch(addTaskRequest(data));
  }

  async function loadSchedule() {
    const header = {
      headers: session.get(),
    };
    const response = await api.get(`/tasks/${queryString}`, header);
    session.set(response.headers);
    return response.data;
  }

  const goBack = () => {
    history.push('/dashboard');
  }

  return (
    <Container>
      <Form initialData={task ? task : {}} onSubmit={handleSubmitTask}>
        <strong>Titulo</strong>
        <Input name="name" type="text" placeholder="Insira um titulo" />
        <strong>Descricao</strong>
        <Input
          name="description"
          type="text"
          placeholder="Insira uma descricao"
        />
        <strong>Data</strong>
        <Input name="end_time" type="date" placeholder="Insira uma data" />

        <button type="submit">Salvar</button>
      </Form>

      <button onClick={() => goBack()} type="button">
        Voltar
      </button>
    </Container>
  );
}
